import {Component, HostListener, Input} from '@angular/core';

@Component({
  selector: 'app-title',
  templateUrl: './title.component.html',
  styleUrls: ['./title.component.css']
})
export class TitleComponent {
  @Input() titleString:string = "";
  @Input() imgPath:string = "";
  @HostListener('window:scroll')
  onScroll(): void {
    var banners = document.getElementsByClassName("banner");
    for (var i = 0; i < banners.length; i++) {
      var banner = banners[i];
      var animBanner = Math.min(Math.max((banner.getBoundingClientRect().top + banner.offsetHeight) / (height + banner.offsetHeight), 0), 1);
      banners[i].style.backgroundPosition = "50% " + -500 * animBanner + "px";
    }
  }
}
<div id="skillsTitle" setBackgroundImage [imageUrl]="imgPath">
    <div class="content">
        <h2>{{titleString}}</h2>
        <p>La liste des mes compétences que j'ai acquises personnellement ou grâce à mes études</p>
    </div>
</div>
