import { Component } from '@angular/core';

@Component({
  selector: 'app-banner',
  templateUrl: './banner.component.html',
  styleUrls: ['./banner.component.css']
})
export class BannerComponent {
  arrowPath = "assets/logos/arrow.png";
}
<div id="banner">
  <div class="midBanner">
    <div id="nameBanner">
      <h1>Valentin MOIZAN</h1>
    </div>
    <div id="descBanner">
      <h2>Ingénieur diplômé ISEN, passioné par l'informatique</h2>
    </div>
  </div>
  <img id="arrowDown" src="{{arrowPath}}" alt="arrow">
</div>
