import { Component, Input } from '@angular/core';

@Component({
  selector: 'app-skills-box',
  templateUrl: './skills-box.component.html',
  styleUrls: ['./skills-box.component.css']
})
export class SkillsBoxComponent {
  @Input() skills:string[] = [];
  @Input() skillsLogos:string[] = [];
  @Input() logo:string = "";
  @Input() boxTitle:string = "";
}

  <div class="title">{{boxTitle}}</div>
  <div class="skillsList">
    <!--<div class="ski" *ngFor="let skill of skills; index as i;">
      <div class="skillIcon"> <img src="{{skillsLogos[i]}}" alt="{{skill}}"> </div>
      <div class="skillTitle">{{skill}}</div>
    </div>-->
    <div class="ski">
      <div class="skillIcon"> <img src="{{skillsLogos[0]}}" alt="{{skills[0]}}"> </div>
      <div class="skillTitle">{{skills[0]}}</div>
    </div>
    <div class="ski">
      <div class="skillIcon"> <img src="{{skillsLogos[1]}}" alt="{{skills[1]}}"> </div>
      <div class="skillTitle">{{skills[1]}}</div>
    </div>
  </div>
