import {Component, ElementRef, ViewChild} from '@angular/core';
import {ExtraOptions} from "@angular/router";

@Component({
  selector: 'app-banner',
  templateUrl: './banner.component.html',
  styleUrls: ['./banner.component.css']
})
export class BannerComponent {
  @ViewChild("skillsComponent") comp: ElementRef | undefined;
  arrowPath = "assets/logos/arrow.png";
  scrollToElement(el: string): void {
    // @ts-ignore
    document.getElementById(el).scrollIntoView({
      behavior: "smooth",
      block: "start",
      inline: "nearest"
    });
    console.log(this.comp);
    //this.comp.nativeElement.scrollIntoView({behavior: "smooth", block: "start", inline: "nearest"});
  }

}
<div id="banner">
  <div class="midBanner">
    <div id="nameBanner">
      <h1>Valentin MOIZAN</h1>
    </div>
    <div id="descBanner">
      <h2>Ingénieur diplômé ISEN, passioné par l'informatique</h2>
    </div>
  </div>
  <div id="arrowDown" (click)="scrollToElement(skillsComponent)">
    <img src="{{arrowPath}}" alt="arrow" galleryimg="no">
  </div>

</div>
