import {Component, Input} from '@angular/core';

@Component({
  selector: 'app-title',
  templateUrl: './title.component.html',
  styleUrls: ['./title.component.css']
})
export class TitleComponent {
  @Input() titleString:string = "";
  @Input() imgPath:string = "";
}
<div class="title" style="'background: url(' + {{imgPath}} + ')'">
    <div class="content">
        <h2>Compétences</h2>
        <p>La liste des mes compétences que j'ai acquises personnellement ou grâce à mes études</p>
    </div>
</div>
